mapper.update:
update product_baseinfo
set isValid = #{isValid,jdbcType=INTEGER},
checked = #{checked,jdbcType=INTEGER}
where productInnerId = #{productInnerId,jdbcType=BIGINT}

mapper.select:
	select 
	* 
	from product_baseinfo
	where checked=#{checked,jdbcType=INTEGER}
	order by extractTime
  limit #{offset},#{size}

select 

from product_baseinfo
where 1=1
	 and checked=#{checked} 
	 and isValid=#{isValid}  
     and platform=#{platform}  
     and singleKeyword=#{keyword}  
     and (productName like CONCAT('%',#{key},'%') or singleKeyword like CONCAT('%',#{key},'%'))  
     and categoryCode in #{item} 
      
     limit #{size}, #{offset}

	select 
   count(*)
   from product_baseinfo
   where 1=1
   	 and checked=#{checked} 
   	 and isValid=#{isValid}  
        and platform=#{platform}  
        and singleKeyword=#{keyword}  
        and (productName like CONCAT('%',#{key},'%') or singleKeyword like CONCAT('%',#{key},'%'))  
        and categoryCode in #{item}
