mapper.update:
update product_baseinfo
set isValid = #{isValid,jdbcType=INTEGER},
checked = #{checked,jdbcType=INTEGER}
where productInnerId = #{productInnerId,jdbcType=BIGINT}

mapper.select:
select
* 
from product_baseinfo where productName like #{word} or platform like #{word} or singleKeyword like #{word}
 and checked=#{checkedNum}
order by extractTime
limit #{offset},#{size}

select
count(*) 
from product_baseinfo where productName like #{word} or platform like #{word} or singleKeyword like #{word}
 and checked=#{checkedNum}
order by extractTime

	select 
	* 
	from product_baseinfo
	where checked=#{checked,jdbcType=INTEGER}
	order by extractTime
  limit #{offset},#{size}

select 

from product_baseinfo
where 1=1
	 and checked=#{checked} 
	 and isValid=#{isValid}  
     and platform=#{platform}  
     and singleKeyword=#{keyword}  
     and (productName like CONCAT('%',#{key},'%') or singleKeyword like CONCAT('%',#{key},'%'))  
     and categoryCode in #{item} 
      
     limit #{size}, #{offset}

	select 
   count(*)
   from product_baseinfo
   where 1=1
   	 and checked=#{checked} 
   	 and isValid=#{isValid}  
        and platform=#{platform}  
        and singleKeyword=#{keyword}  
        and (productName like CONCAT('%',#{key},'%') or singleKeyword like CONCAT('%',#{key},'%'))  
        and categoryCode in #{item}

SELECT  FROM product_baseinfo WHERE 1=1  and checked=#{checkedNum} and platform=#{platform} and singleKeyword IN 
(SELECT 三级 FROM threeclassificationtable WHERE 1=1  and 零级=#{level0} and 一级=#{level1}
 and 二级=#{level2} and 三级=#{level3}) order by extractTime limit #{offset},#{size}

SELECT count(productInnerId) FROM product_baseinfo WHERE 1=1  and checked=#{checkedNum} and platform=#{platform} and singleKeyword IN 
(SELECT 三级 FROM threeclassificationtable WHERE 1=1  and 零级=#{level0} and 一级=#{level1}
 and 二级=#{level2} and 三级=#{level3})

