mapper.select:
select 
dataID,productInnerId,productActualID,website,productName,errorInfo,keyword,productURL,productDescription,shelveDate,weight,origin,province,city,category,specialtyCategory,brand,factoryName,factoryAddress,series,specification,deliveryStartArea,productPrice,productPromPrice,monthSaleCount,commentCount,storeActualID,storeName,storeURL,shopkeeper,companyName,storeLocation,expirationDay,produceDay,category1,category2,category3,goodCommentCount,midCommentCount,badCommentCount,SaleCount,pictureCommentCount,additionCommentCount,extractTime,TaskDataID,isValid,checked,userName
from data2 WHERE ( productName like #{word} or keyword like #{word} or userName like #{word})
 and checked=#{checkedNum} and website=#{platform}and keyword IN 
(SELECT 三级 FROM threeclassificationtable WHERE 1=1  and 零级=#{level0} and 一级=#{level1}
 and 二级=#{level2} and 三级=#{level3})
order by dataID desc
 limit #{offset},#{size}

select
count(dataID)
from data2 WHERE ( productName like #{word} or keyword like #{word} or userName like #{word})
 and checked=#{checkedNum} and website=#{platform}and keyword IN 
(SELECT 三级 FROM threeclassificationtable WHERE 1=1  and 零级=#{level0} and 一级=#{level1}
 and 二级=#{level2} and 三级=#{level3})

select `a`.`storeinnerid` ,`a`.`storeactualid` ,`a`.`storeURL` ,`a`.`stor_platform` ,`a`.`storeName`,IFNULL(`b`.`companyName`,'无') companyName,`a`.`store_salse` 
,`a`.`sum(a.monthSaleCount)` AS counts  ,a.sku  from (`店铺季度销售排行` `a` join `store_baseinfo` `b`) where ((`a`.`loc_province` = '重庆') and 
(`a`.`storeinnerid` = `b`.`storeInnerId`)) order by `a`.`store_salse` desc  limit #{offset},#{size}

  select `a`.`platform` AS `platform`,`b`.`一级` AS `一级`,sum(`a`.`totalsale`) AS `销售额`,
sum(`a`.`monthSaleCount`) AS `销量`,count(*) as SKU 
from (`产品月度销售数据` `a` join `threeclassificationtable` `b`) where (`b`.`三级` = `a`.`singleKeyword`)
group by `b`.`一级`,`a`.`platform` order by `销售额` desc  limit #{offset},#{size}

select '所有' selectTimeb.year selectTimeCONCAT_WS(".",b.year,b.monthOfYear) selectTimeCONCAT_WS(".",b.year,b.monthOfYear,b.dayOfMonth) selectTime ,a.platform,count(DISTINCT a.productInnerId) as 数量  from (select platform,productInnerId from product_baseinfo where isValid=1) a,(select productInnerId,year,monthOfYear,dayOfMonth from productmonitor where 1=1  AND year=#{productYear} AND year=#{productYear} AND monthOfYear=#{productMonth} AND year=#{productYear} AND monthOfYear=#{productMonth} AND dayOfMonth=#{productDay} ) b
WHERE a.productInnerId=b.productInnerId  AND b.`year`=#{productYear} AND b.`year`=#{productYear} AND b.monthOfYear=#{productMonth} AND b.`year`=#{productYear} AND b.monthOfYear=#{productMonth} AND b.dayOfMonth=#{productDay} 
GROUP BY a.platform
 limit #{offset},#{size}

    select count(*) from (select platform from (select platform,productInnerId from product_baseinfo where isValid=1) a,(select productInnerId,year,monthOfYear,dayOfMonth from productmonitor where 1=1  AND year=#{productYear} AND year=#{productYear} AND monthOfYear=#{productMonth} AND year=#{productYear} AND monthOfYear=#{productMonth} AND dayOfMonth=#{productDay} ) b
	WHERE a.productInnerId=b.productInnerId  AND b.`year`=#{productYear} AND b.`year`=#{productYear} AND b.monthOfYear=#{productMonth} AND b.`year`=#{productYear} AND b.monthOfYear=#{productMonth} AND b.dayOfMonth=#{productDay} 
	GROUP BY a.platform) c

select '所有' selectTimeb.year selectTimeCONCAT_WS(".",b.year,b.monthOfYear) selectTimeCONCAT_WS(".",b.year,b.monthOfYear,b.dayOfMonth) selectTime ,platform,count(a.productInnerId) as 数量   from `产品月度销售数据` a,(select productInnerId,year,monthOfYear,dayOfMonth from productmonitor where 1=1  AND year=#{productYear} AND year=#{productYear} AND monthOfYear=#{productMonth} AND year=#{productYear} AND monthOfYear=#{productMonth} AND dayOfMonth=#{productDay} ) b
WHERE a.productInnerId=b.productInnerId  AND b.`year`=#{productYear} AND b.`year`=#{productYear} AND b.monthOfYear=#{productMonth} AND b.`year`=#{productYear} AND b.monthOfYear=#{productMonth} AND b.dayOfMonth=#{productDay} 
GROUP BY a.platform
 limit #{offset},#{size}

    select count(*) from (select platform as 数量 from `产品月度销售数据` a,(select productInnerId,year,monthOfYear,dayOfMonth from productmonitor where 1=1  AND year=#{productYear} AND year=#{productYear} AND monthOfYear=#{productMonth} AND year=#{productYear} AND monthOfYear=#{productMonth} AND dayOfMonth=#{productDay} ) b
	WHERE a.productInnerId=b.productInnerId  AND b.`year`=#{productYear} AND b.`year`=#{productYear} AND b.monthOfYear=#{productMonth} AND b.`year`=#{productYear} AND b.monthOfYear=#{productMonth} AND b.dayOfMonth=#{productDay} 
	GROUP BY a.platform) a

	select `b`.`一级` AS `一级`,sum(`a`.`totalsale`) AS `销售额`,sum(`a`.`monthSaleCount`) AS `销量`,count(*) as SKU from (`产品月度销售数据` `a` join `threeclassificationtable` `b`)
 where (`b`.`三级` = `a`.`singleKeyword`) group by `b`.`一级` order by `销售额` desc
	 limit #{offset},#{size}

select `b`.`一级` AS `一级`,sum(`a`.`totalsale`) AS `销售额`,sum(`a`.`monthSaleCount`) AS `销量`,count(0) AS `商品数量` from (`产品月度销售数据` `a` join `threeclassificationtable` `b`) 
where ((`a`.`singleKeyword` = `b`.`三级`) and (`b`.`零级` = '生鲜')) group by `b`.`一级` order by 销售额 desc
 limit #{offset},#{size}

select `b`.`一级` AS `一级`,sum(`a`.`totalsale`) AS `销售额`,sum(`a`.`monthSaleCount`) AS `销量`,count(0) AS `商品数量` from (`产品月度销售数据` `a` join `threeclassificationtable` `b`) 
where ((`a`.`singleKeyword` = `b`.`三级`) and (`b`.`零级` = '非生鲜')) group by `b`.`一级` order by 销售额 desc
 limit #{offset},#{size}

select `重庆农产品各省店铺分布`.`loc_province` AS `省份`,count(`重庆农产品各省店铺分布`.`loc_province`) AS `店铺数量` from `重庆农产品各省店铺分布` group by `重庆农产品各省店铺分布`.`loc_province` 
order by count(`重庆农产品各省店铺分布`.`loc_province`) desc
 limit #{offset},#{size}
